jinja_template:
{{- bos_token if bos_token is defined else "" -}}

{# Handle optional system message first #}
{%- if messages and messages[0].role == "system" %}
  {{ "<|start_header_id|>system<|end_header_id|>

" }}
  {{ messages[0].content | trim + "<|eot_id|>" }}
  {%- set messages = messages[1:] %}
{%- endif %}

{# Render all remaining messages (user, assistant, etc.) #}
{%- for message in messages %}
  {%- set header = "<|start_header_id|>" + message.get("name", message.role) + "<|end_header_id|>

" %}
  {{ header }}
  {{ message.content | trim + "<|eot_id|>" }}
{%- endfor %}

{# Optional generation prompt for next assistant message #}
{%- if add_generation_prompt %}
  {# Try to get the assistant name from last assistant message #}
  {%- set assistant_name = "psychologist" %}
  {%- for m in messages | reverse %}
    {%- if m.role == "assistant" and m.get("name") %}
      {%- set assistant_name = m.get("name") %}
      {%- break %}
    {%- endif %}
  {%- endfor %}
  {{ "<|start_header_id|>" + assistant_name + "<|end_header_id|>

" }}
{%- endif %}
